import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";
import { remove } from "lodash";


const initialState = {
    cart_id: null,
    member_id: null,
    cart_items: [],
    cart_products: []
};

const fetchCart = (state, action) => {
  console.log(action.payload.cart_items)
  let newItems = []
  action.payload.cart_items.map(item => {
    newItems.push({...item})
  })
  let newProducts = []
  action.payload.products.map(prod => {
    newProducts.push({...prod})
  })
  return updateObject(state, {
    cart_id: action.payload.id,
    member_id: action.payload.member_id,
    cart_items: [...newItems],
    cart_products: [...newProducts]
  })
}

const addProductToCart = (state, action) => {
  const updatedCart = [...state.cart_items, action.payload]
  return updateObject(state, {
    cart_items: updatedCart
  })
}

const removeProductFromCart = (state, action) => {
  const updatedCart = [...action.payload.cart_items];
  return updateObject(state, {
    cart_items: updatedCart,
  });
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.FETCH_CART:
      return fetchCart(state, action);
    case actionTypes.ADD_PRODUCT_TO_CART:
      return addProductToCart(state, action);
    case actionTypes.REMOVE_PRODUCT_FROM_CART:
      return removeProductFromCart(state, action);
    default:
      return state;
  }
};

export default reducer;
