import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import api from "../store/apis/api";

import Favorite from "./Favorite";

class Favorites extends React.Component {
  state = {
    favorites: [],
    products: [],
    brands: []
  }

  componentDidMount() {
      this.fetchBrands()
    this.fetchFavorites(this.props.userId)
    this.fetchProducts()
  }
      fetchBrands = () => {
    api.get(`/brands/`).then((resp) => this.setState({ brands: resp.data }));
  };

  fetchFavorites = (user_id) => {
     api
    .get(`/users/${user_id}/favorites`).then(resp => this.setState({favorites: resp.data}))
  }

  fetchProducts = () => {
    api
      .get(`/products/`)
      .then((resp) => this.setState({ products: resp.data }));
  };

  init =  () => {
    this.props.initFavorites(this.props.userId);
    this.props.initBrands();
  }
  findBrand = (product) => {
    console.log(this.props)
    let brand = this.state.brands.filter(
      (brand) => brand.id === product.brand_id
    );
    return brand;
  };

  renderList = () => {

     this.state.products.map((fave) => {
      let brand = this.findBrand(fave)
      return (
        <Favorite
            brand={brand}
            key={fave.id}
            product={fave}
          />
      );
     })
  };

  render() {
    // let renderFavorites = null;
    // if (!this.state.favorites.length <= 0) {
    //   renderFavorites = this.renderList()
    // } else if (this.state.favorites.length > 0) {
    //   renderFavorites = <div>no user found</div>
    // }
    // console.log(this.props);
    return (
      <div>
        <div className='section-products'>{this.renderList()}</div>
      </div>
    );
  }
}

export default Favorites;

