import api from "../apis/api";
import _ from "lodash";
import * as actionTypes from "./actionTypes";
import * as actions from "../actions/index";

export const fetchProdByBrand = (brand_id) => async (dispatch) => {
  const response = await api.get(`/brands/${brand_id}/products`);
  dispatch({
    type: actionTypes.FETCH_PRODUCTS_BY_BRAND,
    payload: response.data,
  });
};

export const fetchProdByCategory = (category) => async (dispatch) => {
  const response = await api.get(`/products/`, {
    params: {
      category: category,
    },
  });
  dispatch({
    type: actionTypes.FETCH_PRODUCTS_BY_CATEGORY,
    payload: response.data,
  });
};

export const fetchProdBySize = (size) => async (dispatch) => {
  const response = await api
    .get(`/products/`, {
      params: {
        size: size,
      },
    })
    .then(
      dispatch({
        type: actionTypes.FETCH_PRODUCTS_BY_SIZE,
        payload: response.data,
      })
    );
};

export const fetchProductsFailed = () => {
  return {
    type: actionTypes.FETCH_PRODUCTS_FAILED,
  };
};

export const setProducts = (products) => {
  return {
    type: actionTypes.SET_PRODUCTS,
    products: products,
  };
};

export const setShowProduct = (product) => async dispatch => {
  console.log(product);
  dispatch(actions.fetchBrand(product.brand_id));
};

export const initProducts = () => async (dispatch) => {
  await api
    .get(`/products/`)
    .then((resp) => {
      dispatch(setProducts(resp.data));
    })
    .catch((error) => {
      dispatch(fetchProductsFailed());
    });
};

export const fetchShowProduct = (prod_id) => async (dispatch) => {
  console.log("in fetchSHow");
  await api
    .get(`products/${prod_id}`)
    .then((resp) => {
      dispatch(setShowProduct(resp.data));
    })
    .catch((error) => {
      dispatch(fetchProductsFailed());
    });
};

export const fetchFaveProduct = (prod_id) => async (dispatch) => {
  const response = await api.get(`products/${prod_id}`);
  dispatch({ type: actionTypes.FETCH_FAVE_PROD, payload: response.data });
};

export const fetchBrand = (brand_id) => async (dispatch) => {
  const response = await api.get(`/brands/${brand_id}`);
  dispatch({ type: actionTypes.FETCH_BRAND, payload: response.data });
};

