import api from "../apis/api";
import * as actionTypes from "./actionTypes";
import * as actions from "../actions/index";

import _ from "lodash";

export const initFavorites = () => async dispatch =>{
  console.log('in init')
  dispatch(startFetchFavorites())
   await api
    .get(`/users/${user_id}/favorites`)
    .then((resp) => {
      dispatch(setFavorites(resp.data))
    })
    .catch((error) => {
      dispatch(fetchFavoritesFailed())
    });

};

export const startFetchFavorites = () => {
  return {
    type: actionTypes.START_FETCH_FAVORITES
  }
}

export const fetchFavoritesFailed = () => {
  return {
    type: actionTypes.FETCH_FAVORITES_FAILED,
  };
};

export const setFavorites = (favorites)=> {
  // console.log(favorites);
  // const products = [];
  //  favorites.map((fave) => products.push({...fave.product}));
  return {
    type: actionTypes.SET_FAVORITES,
    payload: favorites
  };
};

export const deleteFavorite = (favorite_id) => async (dispatch, getState) => {
  let user_id = getState().auth.userId
console.log(user_id)
  const response = await api
    .delete(`/users/${user_id}/favorites/${favorite_id}`, {
      params: {
        user_id: user_id.value
      }
    })
    .then((data) => console.log(data));
  dispatch({ type: actionTypes.DELETE_FAVORITE, payload: favorite_id });
};

export const createFavorite = (user_id, product_id)  => async dispatch => {
  await api
    .post(`/users/${user_id}/favorites`, {
      member_id: user_id,
      product_id: product_id,
    })
    .then(response =>
      dispatch({ type: actionTypes.CREATE_FAVORITE, payload: response.data })
    );

};
