import React from "react";
import { fetchProdById } from "../store/actions/ProductsActions";
import { fetchFavorites } from '../store/actions/FavoritesActions'
import { connect } from "react-redux";

import  Favorite  from "./Favorite";

class Favorites extends React.Component {

  componentDidMount() {
    this.props.fetchFaves(23).then(
    this.props.favorites.forEach(fave => this.props.fetchProd(fave.product_id)))
  }
  render() {
    console.log(this.props)
    return (
      <div>
        {this.props.favorites.map((fave) => {
          return <Favorite brands={this.props.brands} fave={fave} product={this.props.products.filter(prod => prod.id === fave.product_id)} />;
        })}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state.favoriteState);
  return {
    favorites: state.favoriteState,
    products: state.productState.productSelection,
    brands: state.brandState.brands
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchFaves: (user_id) => dispatch(fetchFavorites(user_id)),
    fetchProd: (product_id) => dispatch(fetchProdById(product_id))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
