import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";

const OrderContainer = (props) => {
  handleClick = (e) => {
    e.preventDefault()
    // this.props.fetchShowProductSizes(6, 33)
    //console.log(props);
    //props.addProductToCart();
  };

    console.log(props.sizes);

   return (
    <React.Fragment>
      <div className='product-add-to-bag-wrapper'>
        <div className='product-add-to-bag-img-wrapper'>
          <img
            onClick={(e) => handleClick(e)}
            alt=''
            color={props.product.colorway}
            id={props.product.product_number}
            src={props.images[0]}
          />

          <img
            onClick={(e) => handleClick(e)}
            alt=''
            color={props.product.colorway}
            id={props.product.product_number}
            src={props.product.images[2]}
          />
        </div>
      </div>
      <div className='product-add-to-bag-wrapper'>
        {this.props.sizes.map((size) => {
          return (
            <div
              onClick={(e) => this.handleClick(e)}
              className='product-show-title'
              id="size-button"
            >
              {size}
            </div>
          );
        })}
      </div>
        <div onClick={(e) => this.handleClick(e)}>ADD TO FAVORITES</div>
    </React.Fragment>
  )}


const msp = (state) => {
  console.log(state)
  return {
    cart: state.cartState,
  };
};

const mdp = (dispatch) => {
  return {
    fetchCart: (user_id) => dispatch(actions.fetchCart(user_id)),
    addProductToCart: (cart_id, product_id) =>
      dispatch(actions.addProductToCart(cart_id, product_id)),
    fetchUser: (user_id) => dispatch(actions.fetchUser(user_id)),
    createFavorite: (user_id, product_id) =>
      dispatch(actions.createFavorite(user_id, product_id)),

    // fetchShowProductSizes: (brand_id, product_id) =>
    //   actions.fetchShowProductSizes(brand_id, product_id),
  };
};
export default connect(msp, mdp)(OrderContainer);
