import api from "../apis/api";
import * as actionTypes from "./actionTypes";

export const addSizeToCart = (size) => async (dispatch) => {
  const response = await api.patch(`/sizes/${size.id}`, {
    headers: { "Access-Control-Allow-Origin": "*" },
  });
  dispatch({ type: "ADD_SIZE_TO_CART", payload: response.data });
};

export const fetchShowProductSizes = (brand_id, product_id) => {
  console.log("in fetch sizes", product_id, brand_id);
   api
    .get(`brands/${brand_id}/products/${product_id}/sizes`)
    .then((resp) => {
      console.log(resp.data, actionTypes.SET_SHOW_PRODUCT_SIZES)
      (setShowProductSizes(resp.data));
    });
};


export const setShowProductSizes = (sizes) => {
  console.log("in this baby function")
  return {
    type: actionTypes.SET_SHOW_PRODUCT_SIZES,
    sizes: sizes,
  };
};
