import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";

const PlanCard = (props) => {

  const clickHandler = (e) => {
    e.preventDefault();
    props.initPlanMembership(props.plan.id);
  };

  const renderText = () => {

    //guests
    if (props.current_plan === null && props.current_plan === null) {
      return "Try Now";
    }

    //members with or without plan membership
    return props.plan.id !== props.current_plan.plan_id
      ? "Try Now"
      : "Current Plan";
  };

  const renderId = () => {

    //guests
    if (props.current_plan === null && props.current_plan === null) {
      return "plan-option";
    } else if (

      //determine for user subscription
      props.current_plan !== undefined &&
      props.current_plan !== null
    ) {
      return props.plan.id !== props.current_plan.plan_id
        ? "plan-option"
        : "current-plan";
    }
  };

  return (
    <div className='plan-card'>
      <React.Fragment>
        <div className='plan-card-details'>
          <ul className='plan-title'>Up to</ul>
          <p>{props.plan.items} </p>
          <span>items per month</span>
          <li className='plan-description'>{props.plan.description}</li>
        </div>
        <hr id='order-hr' />
      </React.Fragment>
      <div className='plan-card-features'>
        <ul className='plan-bullet-points'>
          {props.plan.features.split(",").map((feature) => {
            return (
              <li>
                <ion-icon name='checkmark'></ion-icon>
                <span>{feature}</span>
              </li>
            );
          })}
        </ul>
      </div>
      <div id='plan-button'>
        <button
          plan={props.plan.id}
          id={renderId()}
          value='button'
          onClick={(e) => clickHandler(e)}
        >
          {renderText()}
        </button>
      </div>
      <div className='plan-price'>
        <span>{props.plan.price_string}</span> <p>/month</p>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    current_plan: state.plan.current_plan,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initPlanMembership: (plan_id) =>
      dispatch(actions.initPlanMembership(plan_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PlanCard);
