import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";

class Favorite extends React.Component {
  componentDidMount() {
    //this.props.fetchProd(this.props.fave.product_id)
  }

  render() {
    console.log(this.props.brand);
    return (
      <div className="section-products">
        <div className="product-card">
          <li>{this.props.product[0].name}</li>
          <li>{this.props.product[0].price_string}</li>
          <img src={this.props.product[0].images[0]} alt=""/>
          <button>Delete Favorite</button>
          <button>See Product</button>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state);
  return {
    product: state.productState.productSelection.filter(
      (product) => product.id === ownProps.fave.product_id),
      brand: state.brandState.brands.filter(brand => brand.id === ownProps.product.brand_id)
    ,
  };
};

const mapDispatchToProps = (dispatch) => {
  return { fetchProd: (prod_id) => dispatch(actions.fetchProdById(prod_id)) };
};
export default connect(mapStateToProps, mapDispatchToProps)(Favorite);
