import React from 'react'
import CreateUserPlanForm from './createUserPlanForm'
import { connect, useDispaatch } from "react-redux";
import * as actions from "../store/actions/index";
import PlanCard from './PlanCard'


export const Plans = (props) => {
 let dispatch = userDispatch()
  const clickHandler = (e) => {
    e.preventDefault()
    dispatch(props.fetchPlans())
    console.log(props)
  }
  return(

    <div>
      <div className="row">I"m in the plans container</div>
      <CreateUserPlanForm/>{console.log(props)}
      <PlanCard/>
      <div><button onClick={(e) => clickHandler(e)}>find your style</button></div>
    </div>
  )
}


const mapStateToProps = (state) => {
  return {
    loading: state.plans.loading,
    error: state.plans.error,
    plans: state.plans.all
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    userPlanStart: (user_id, plan_id) =>
      dispatch(actions.userPlanStart(user_id, plan_id)),
    fetchPlans: () => dispatch(actions.fetchPlans()),
  };
};



export default connect(mapStateToProps, mapDispatchToProps)(Plans);
