import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import OrderContainer from "./OrderContainer";

class ProductShow extends React.Component {
  componentDidMount() {
    this.props.fetchShowProduct(this.props.match.params.product_id);

  }

  renderProduct = () => {
    console.log(this.props.showProduct)
    return (
      <section className='product-show'>
        <div className='col span-2-of-3'>
          <div>
            {!this.props.showProduct ? <div>hi</div> : this.props.showProduct.images.split(",").map((image) => {
              return (
                <div key={image.id}>
                  <img
                    onClick={this.props.addProductToCart}
                    key={image.id}
                    src={image}
                  />
                </div>
              );
            })}
          </div>
        </div>
        <div className='span-1-of-3' id='product-text'>
          <p className='product-brand-show-title'>{this.props.brand.name}</p>

          <p className='product-show-subtitle'>{this.props.showProduct.name}</p>

          <p className='product-show-subtitle'>
            {this.props.product.price_string}
          </p>
          <OrderContainer
            brand={this.props.brand}
            product={this.props.showProduct}
            sizes={this.props.sizes}
          />
          <div className='product-show-description-wrapper'>
            <p>{this.props.showProduct.description}</p>
          </div>
        </div>
      </section>
    );
  };

  render() {
    return <React.Fragment><div className='section-products'>
          {" "}
          <br />
          {this.renderProduct}
        </div></React.Fragment>;

