  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state.product, ownProps);
  return {
    select: state.product.select,
    showProduct: state.product.show,
    brand: state.brand.brand,
    sizes: state.size.sizes
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    cart: (product_id) => dispatch(actions.addProductToCart(product_id)),
    fetchShowProduct: (product_id) => dispatch(actions.fetchShowProduct(product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductShow);
