import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import ProfileHeader from "./ProfileHeader";
import Favorite from "./Favorite";

class Profile extends React.Component {
  componentDidMount() {
    this.props.fetchUser();
    this.props.fetchUserPlan();
    this.props.initFetchPlans();
    this.props.initFavorites();
  }

  renderUser = () => {
    return this.props.current_user && this.props.current_user ? (
      <>
        <section className='profile-section-container'>
          <div className='profile-section'>
            <p className='profile-photo' />
            <div className='profile-title'>
              <p>
                <strong>{this.props.current_user.name}</strong>
              </p>
              <p className='profile-subtitle'>Verses member since June 2016</p>
            </div>
          </div>
        </section>
      </>
    ) : null;
  };

  renderPlan = () => {
    let user_plan;
    if (this.props.current_plan && this.props.current_plan) {
      user_plan = this.props.plans.filter(
        (plan) => plan.id === this.props.current_plan.plan_id
      )[0];

      return (
        <div className='profile-plan'>
          <p>Current Plan</p>
          <strong>{user_plan.items} </strong>items per month |
          <strong>{user_plan.price_string}</strong>/month
        </div>
      );
    }
  };

  findBrand = (product) => {
    if (product !== "undefined" && product !== undefined) {
      let brand = this.props.brands.filter(
        (brand) => brand.id === product.brand_id
      );
      return brand[0];
    }
  };

  renderFavorites = () => {
    if (
      !!this.props.favorites && !!this.props.favorites 
    )
    return (
      <div className=' product-nav-centered'>
          <p className='brands-subtitle' id='message'>
            Items added to your Favorites will be saved here.
          </p>
        </div>
      );
      return this.props.favorites.map((fave) => {
        let product = fave["product"];
        let brand = this.findBrand(product);
        return (
          <Favorite
          brand={brand}
          fave={fave}
          product={product}
          size={fave.size}
          />
          );
        });
      };
  render() {
    return (
      <>
        <div className='profile-header-wrapper'>
          <div className='profile-header-container'>
            <ProfileHeader />
          </div>
          <div className='spacer' />
        </div>
        <div className='profile-section-wrapper'>
          {this.renderUser()}
          {this.renderPlan()}
          {this.renderFavorites()}
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    current_user: state.auth.current_user,
    current_plan: state.plan.current_plan,
    favorites: state.favorite.favorites,
    brands: state.brand.select,
    products: state.product.select,
    plans: state.plan.select,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUser: () => dispatch(actions.fetchUser()),
    fetchUserPlan: () => dispatch(actions.fetchUserPlan()),
    initFetchPlans: () => dispatch(actions.initFetchPlans()),
    initFavorites: () => dispatch(actions.initFavorites()),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Profile);
