import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";

import  Favorite  from "./Favorite";

class Favorites extends React.Component {

  componentDidMount() {
    this.props.fetchFaves(23)
  }
  render() {
    return (
      <div>
        {this.props.favorites.map((fave) => {
          return <Favorite fave={fave} product={this.props.products.filter(prod => prod.id === fave.product_id)} />;
        })}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    favorites: state.favoriteState.favorites,
    products: state.productState.productSelection
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchFaves: (user_id) => dispatch(actions.fetchFavorites(user_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
