import api from "../apis/api";
import * as actionTypes from "./actionTypes";


export const deleteFavorite = (user_id, favorite) => async dispatch => {
  const response = await api.delete(`/users/${user_id}/favorites/${favorite.id}`).then(data => console.log(data))
  dispatch({type: actionTypes.DELETE_FAVORITE, payload: response})
};

export const createFavorite = (user_id, product_id) => async dispatch => {
  const response = await api.post(`/users/${user_id}/favorites`, {
    member_id: user_id,
    product_id: product_id
  })
  dispatch({type: actionTypes.CREATE_FAVORITE, payload: response.data})
};

export const fetchFavorites = (user_id) => async (dispatch,) => {
  const response = await api.get(`/users/${user_id}/favorites`, {
    member_id: user_id,
  });
  dispatch({ type: actionTypes.FETCH_FAVORITES, payload: response.data });
};


export const fetchUserFavorites = () => async (dispatch, getState) => {
  await dispatch(fetchFavorites())

}
