import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";

import Favorite from "./Favorite";

class Favorites extends React.Component {
  componentDidMount() {
    actions.initFavorites(this.props.userId)
    console.log(this.props)
  }

  // fetchFaveBrands = ()

  findBrand = (product) => {
    let brand = this.props.brands.filter(
      (brand) => brand.id === product.brand_id
    );
    return brand;
  };

  renderList = () => {
    console.log("render list", this.props.brands);
    return this.props.products.map((fave) => {
      let brand = [...this.findBrand(fave)]
      return (
        <Favorite
            brand={brand}
            key={fave.id}
            product={fave}
          />
      );
    });
  };

  render() {
    // console.log(this.props);
    return (
        <div>
          <div className="section-products">
              {this.renderList()}
          </div>
        </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state);
  return {
    favorites: state.favorite.favorites,
    products: state.favorite.products,
    brands: state.brand.brands,
    userId: state.auth.userId,
    error: state.favorite.error
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initFavorites: actions.initFavorites(),
    setShowProduct: actions.setShowProduct(),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
