import api from "../apis/api";
import * as actionTypes from "../actions/actionTypes";

export const addProductToCart = (user_id, product_id, size) => async (
  dispatch
) => {
  await api
    .patch(`/users/${user_id}/cart/add_item`, {
      product_id: JSON.stringify(product_id),
      size: size,
    })
    .then((resp) =>
      dispatch({
        type: actionTypes.ADD_PRODUCT_TO_CART,
        payload: resp.data,
      })
    );
};

export const initCart = () => async (dispatch, getState) => {
  dispatch(startFetchCart())
  let user = getState().auth.userId
  console.log(user)
 await api.get(`/users/${user}/cart`).then(response => {
   console.log(response)
   dispatch(setCart(response.data))
 })
 .catch((error) => {
   dispatch(fetchCartFailed(error))
 })
}

export const startFetchCart = () => {
  return {
    type: actionTypes.START_FETCH_CART,
  };
};

export const refreshCart = () => async (dispatch, getState) => {
  let user = getState().auth.userId
  await api.delete(`/users/${user}/cart/refresh`)
  .then(response => dispatch(setCart(response)))
}
export const fetchCartFailed = (error) => {
  return {
    type: actionTypes.FETCH_CART_FAILED, payload: error
  };
};

export const setCart = (cart) => {
  console.log(cart)
  return {
    type: actionTypes.SET_CART,
    payload: cart,
  };
};

export const removeProductFromCart = (cart_item) => async (
  dispatch, getState
  ) => {
    let user = getState().auth.userId
  await api
    .patch(`/users/${user}/cart/cart_items/delete_item`, [...cart_item])
    .then(
      dispatch({
        type: actionTypes.REMOVE_PRODUCT_FROM_CART,
        payload: [cart_item],
      })
    );
};
