import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import { Redirect } from "react-router-dom";
import Favorites from './Favorites'

class Profile extends React.Component {

  state = {
    favorites: [],
    products: [],
    brands: []
  }


  componentDidMount() {
    // this.props.fetchUser(this.props.userId);
    // this.props.initFavorites(this.props.userId);
    this.fetchBrands()
    this.fetchFavorites(this.props.userId)
    this.fetchProducts()
  }


  render() {
    let renderUser = null;
    if (this.props.current_user) {
      renderUser = <div>{this.props.current_user.name}</div>;
    } else if (!this.props.current_user) {
      renderUser = <div>no user found</div>;
    }
    return <div>{renderUser}</div>;
  }
}
const mapStateToProps = (state) => {
  return { userId: state.auth.userId,
    current_user: state.auth.current_user,
    favorites: state.favorite.favorites,
    products: state.favorite.products,
    loading: state.auth.loading};
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUser: (user_id) => dispatch(actions.fetchUser(user_id)),
    initFavorites: (user_id) => dispatch(actions.initFavorites(user_id))

  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Profile);
