import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import CartItem from "./CartItem";

class Cart extends React.Component {
  state = {
    favorites: []
  }
  componentDidMount() {
    // this.props.initCart(this.props.member_id)
    actions.initCart(this.props.member_id)
  }

  findBrand = (product) => {
      let brand = this.props.brands.filter(brand => brand.id === product.brand_id)
      return brand[0]
    }


  renderList = () => {
    return this.props.cart_products.map(prod => {
      let brand = this.findBrand(prod)
      return <CartItem brand={brand} key={prod.id} product={prod} />;
    })
  }

  render() {
    let cart = this.props.loading ? this.renderList() : <div>loading</div>;
    return (
      <React.Fragment>
        <div>
          {this.renderList()}
          {console.log(cart)}
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    cart_id: state.cart.cart_id,
    member_id: state.cart.member_id,
    // cart_items: state.cart.cart_items,
    cart_products: state.cart.cart_products,
    brands: state.brand.select,
    loading: state.cart.loading
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    // initCart: (this.props.member_id) => dispatch(actions.initCart(this.props.member_id)),
    addToCart: (user_id, product_id, size) => dispatch(actions.addProductToCart(user_id, product_id, size)),
    removeProductFromCart: (user_id, product_id, size) =>
      dispatch(actions.removeProductFromCart(user_id, product_id, size)),

  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
