  renderPlan = () => {
    let value;
    if (props.current_user && props.current_user.subscribed) {
      value = <div>Your Plan: {props.current_user.plan_membership.plan_name}</div>;
    } else {
      value = <div>Choose A Plan</div>;
    }
    return value;
  };

  renderUser = () => {if (props.current_user) {
    renderUser = <div>
