import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";


class Favorite extends React.Component {
  state = {
    product: []
  }
  componentDidMount() {
    this.setState({product: this.props.fetchProd(this.props.fave.product_id)})
  }
  render() {
    console.log(this.props);
      return (
      <div>
        <li>

            {<li>{this.props.product.id}</li>}

        </li>
      </div>
    );}

}

const mapStateToProps = (state) => {
  console.log(state)
return {product: state.productState.productSelection.filter(product => product.id === this.props.fave.product_id)}
}

const mapDispatchToProps = (dispatch) => {
  return {fetchProd: (prod_id) => dispatch(actions.fetchProdById(prod_id))}
}
export default connect(mapStateToProps, mapDispatchToProps)(Favorite)
