import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import ProductCard from "./ProductCard";

const Favorite = (props) => {

  const clickHandler = (e) => {
    e.preventDefault()
    let favorite_id = props.fave.id
    let product_id = props.fave.product.id
    let size_id = props.fave.size_id
    let user_id = props.fave.member_id

    console.log(favorite_id, product_id, user_id)
    e.target.value === 'delete' ? props.deleteFavorite(favorite_id) : props.addFavoriteToCart(props.userId, favorite_id, size_id)
  }

    console.log(props)
    return (
        <div>
          <ProductCard
            brand={props.brand}
            fave={props.fave}
            product={props.product}
          />
              <button value="delete" className='product-subtitle' id="product-button" onClick={(e) => clickHandler(e)} >Delete Favorite</button>
              <p className='product-subtitle' id="product-button" onClick={(e) => clickHandler(e)}>Add to Cart</p>
        </div>
    );
}

const mapStateToProps = state => {
  return {
    favorites: state.favorite.select,
    userId: state.auth.userId,
  }
}


const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (user_id, product_id, size_id) =>
      dispatch(actions.addFavoriteToCart(user_id, product_id, size_id)),
    deleteFavorite: (favorite) => dispatch(actions.deleteFavorite(favorite)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorite);
