import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import OrderContainer from "./OrderContainer";

class ProductShow extends React.Component {


  componentDidMount() {
    this.props.fetchShowProduct(this.props.match.params.product_id);
  }

    handleClick = (e) => {
    e.preventDefault() 
    e.target.value === 'addToCart' ? this.props.addToCart(this.props.showProduct.id) : this.props.addToFavorites()
  };
  renderProduct = () => {
    console.log(this.state);

    console.log(this.props.sizes)

    return (
      <section className='product-show'>
        <div className='col span-2-of-3'>
          <div>
            {!this.props.showProduct ? (
              <div>hi</div>
            ) : (
              this.props.images.map((image) => {
                return (
                  <div key={this.props.images[image]}>
                    <img
                      key={image.id}
                      src={image}
                      images={this.props.images}
                    />
                  </div>
                );
              })
            )}
          </div>
        </div>
        <div className='span-1-of-3' id='product-text'>
          <p className='product-brand-show-title'>{this.props.brand.name}</p>

          <p className='product-show-subtitle'>{this.props.showProduct.name}</p>

          <p className='product-show-subtitle'>
            {this.props.showProduct.price_string}
          </p>
          <hr />
          <div className='product-add-to-bag-wrapper'>
            {this.props.sizes.map((size) => {
              return (
                <li
                  onClick={(e) => this.handleClick(e)}
                  className='product-show-title'
                  id='size-button'
                >
                  {size}
                </li>
              );
            })}
          </div>
          <ul className='product-actions' onClick={(e) => this.handleClick(e)}>
            <button value="addToCart" id='product-button'>ADD TO CART</button>
            <button value="favorite" id='product-button'>FAVORITE</button>
          </ul>
          <div className='product-show-description-wrapper'>
            <p>{this.props.showProduct.description}</p>
          </div>
            <a id='size-chart' href={this.props.brand.size_chart}>Size Chart
            </a>
        </div>
      </section>
    );
  };

  render() {
    return (
      <React.Fragment>
        <div className='section-products'>
          {" "}
          <br />
          {this.renderProduct()}
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state, ownProps);
  return {
    select: state.product.select,
    showProduct: state.product.show,
    brand: state.brand.show,
    sizes: state.product.sizes,
    images: state.product.images,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (product_id) => dispatch(actions.addProductToCart(product_id)),
    fetchShowProduct: (product_id) =>
      dispatch(actions.fetchShowProduct(product_id)),
    addToFavorites: (user_id, product_id) =>
      dispatch(actions.createFavorite(user_id, product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductShow);
