import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import AccountInfo from "./AccountInfo";
import OrderContainer from "./OrderContainer";

class Profile extends React.Component {
  componentDidMount() {
    this.props.fetchUser();
  }
  renderPlan = () => {
    return this.props.current_user && this.props.current_user.subscribed ? 
        <div>
          Your Plan: {this.props.current_user.plan_membership.plan_name}
        </div>
      : <div>Choose A Plan</div>;
  };

  renderUser = () => {

    return this.props.current_user && this.props.current_user ? 
      (
        <div>
          <div>Welcome, {this.props.current_user.name}</div>
        </div>
      )
     : 
       <div>no user found</div>;
    }

  render() {
    return (
        <div>
          <div>{this.renderUser()}</div>
          <div>{this.renderPlan()}</div>
          <OrderContainer />
          <AccountInfo />
        </div>
    );
  }
};
const mapStateToProps = (state) => {
  return {
    userId: state.auth.userId,
    current_user: state.auth.current_user,
    favorites: state.favorite.favorites,
    products: state.favorite.products,
    loading: state.auth.loading,
    cart: state.cart.cart,
    cart_items: state.cart.cart_items,
  };
};


const mapDispatchToProps = (dispatch) => {
  return {
    fetchUser: () => dispatch(actions.fetchUser()),
  };
};
export default connect(mapStateToProps,mapDispatchToProps)(Profile);

// export default Profile
