import React from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";

const PlanCard = (props) => {

  const planBtnText = () => {
    props.plan_id === current_user.plan.id
  }

   return (
     <div className='col span-1-of-3'>
       <div className='plan-card'>
         <div className='col plan-outer'></div>
         <React.Fragment>
           <ul className='plan-card-details'>
             <li className='plan-title'>{props.plan.name}</li>
             <li>{props.plan.description}</li>
             <li className='plan-price'>{props.plan.price_string}</li>
           </ul>
         </React.Fragment>
       </div>
       <span>
         {props.token ? (
           <Link to='/cart'>
             <button value='TRY NOW'>{planBtnText}</button>
           </Link>
         ) : (
           <Link to='/signup'>
             <button value='TRY NOW'>button</button>
           </Link>
         )}
       </span>
     </div>
   );
}

const mapStateToProps = (state) => {
  return {
    current_user: state.current_user,
    token: state.auth.token,
  };
};
export default connect(mapStateToProps)(PlanCard)
