import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";


import  Favorite  from "./Favorite";

class Favorites extends React.Component {

  componentDidMount() {
    this.props.fetchFaves(23).then(this.props.favorites.forEach(fave => this.props.fetchProd(fave.product_id)))
  }

  renderList = () => {

        return this.props.favorites.forEach((fave) => {

          return <Favorite brands={this.props.brands} fave={fave} product={this.props.products.filter(prod => prod.id === fave.product_id)} />;
        })

  }
  render() {
    console.log(this.props)
    return (
      <div>{this.renderList()}</div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state.favoriteState.favorites);
  return {
    favorites: state.favoriteState.favorites,
    products: state.productState.productSelection,
    brands: state.brandState.brands
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchFaves: (user_id) => dispatch(actions.fetchFavorites(user_id)),
    fetchProd: (product_id) => dispatch(actions.fetchProdById(product_id))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
