import React, { useState } from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";

const ProductShow = (props) => {
  const [size, setSize] = useState('')

  // componentDidMount() {
  //   props.fetchShowProduct(props.match.params.product_id);
  // }

    const handleClick = (e) => {
    e.preventDefault()

    if(e.target.value === 'addToCart') {
        props.addToCart(props.userId, props.showProduct.id) }

    if(e.target.value === 'addToFavorites') {
      props.addToFavorites(props.userId, props.showProduct.id)
    }
  };

  const handleSizeClick = (e) => {
    e.preventDefault()
    console.log(e.target.value)
    setSize(e.target.value)
    console.log(size)
  }
  const renderProduct = () => {
    console.log(state);

    console.log(props.sizes)

    return (
      <section className='product-show'>
        <div className='col span-2-of-3'>
          <div>
            {!props.showProduct ? (
              <div>hi</div>
            ) : (
              props.images.map((image) => {
                return (
                  <div key={props.images[image]}>
                    <img
                      key={image.id}
                      src={image}
                      images={props.images}
                    />
                  </div>
                );
              })
            )}
          </div>
        </div>
        <div className='span-1-of-3' id='product-text'>
          <p className='product-brand-show-title'>{props.brand.name}</p>

          <p className='product-show-subtitle'>{props.showProduct.name}</p>

          <p className='product-show-subtitle'>
            {props.showProduct.price_string}
          </p>
          <hr />
          <div className='product-add-to-bag-wrapper'>
            {props.sizes.map((size) => {
              return (
                <li
                  onClick={(e) => handleSizeClick(e)}
                  className='product-show-title'
                  id='size-button'
                  value={size}
                >
                  {size}
                </li>
              );
            })}
          </div>
          <ul className='product-actions' onClick={(e) => handleClick(e)}>
            <button value="addToCart" id='product-button'>ADD TO CART</button>
            <button value="favorite" id='product-button'>FAVORITE</button>
          </ul>
          <div className='product-show-description-wrapper'>
            <p>{props.showProduct.description}</p>
          </div>
            <a id='size-chart' href={props.brand.size_chart}>Size Chart
            </a>
        </div>
      </section>
    );
  };

  // render() {
    return (
      <React.Fragment>
        <div className='section-products'>
          {" "}
          <br />
          {renderProduct()}
        </div>
      </React.Fragment>
    );
  // }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state, ownProps);
  return {
    select: state.product.select,
    showProduct: state.product.show,
    brand: state.brand.show,
    sizes: state.product.sizes,
    images: state.product.images,
    userId: state.auth.userId,
    cart: state.cart.cart,
    cart_items: state.cart.cart_items 
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (product_id) => dispatch(actions.addProductToCart(product_id)),
    fetchShowProduct: (product_id) =>
      dispatch(actions.fetchShowProduct(product_id)),
    addToFavorites: (user_id, product_id) =>
      dispatch(actions.createFavorite(user_id, product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductShow);
