import api from "../apis/api";
import * as actionTypes from "./actionTypes";

export const fetchBrand = (brand_id) => async (dispatch) => {
  const brand = []
  await api.get(`/brands/${brand_id}`)
  .then(resp => {
    for(let key in resp.data) {
      brand[key] = resp.data[key]
    }
    console.log(brand)
  })
  dispatch({ type: actionTypes.FETCH_BRAND, payload: brand });
};

export const fetchBrands = () => async (dispatch) => {
  const brands = []
  await api.get(`/brands/`)
  .then(resp => {
    for(let key in resp.data) {
      brands[key] = resp.data[key]
    }
  })
  dispatch({ type: actionTypes.FETCH_BRANDS, payload: brands});
};
