import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";
import { remove, isUndefined } from "lodash";


const initialState = {
    cart_id: null,
    member_id: null,
    cart_items: [],
    cart_products: [],
    error: null,
    loading: false,
};

const setCart = (state, action) => {
  if (action.payload !==null ){
  return updateObject(state, {
      cart_id: [...action.payload.id],
      member_id: [...action.payload.member_id],
      cart_items: [...action.payload.cart_items],
      cart_products: [...action.payload.products],
      loading: false,
      error: null
    })
  } 
}

const startFetchCart = (state, action) => {
  return updateObject(state, {
    loading: true, error: null
  })
}

const addProductToCart = (state, action) => {
  const updatedCart = [...state.cart_items, action.payload]
  return updateObject(state, {
    cart_items: updatedCart,
  })
}

const removeProductFromCart = (state, action) => {
  console.log(state, action.payload)
  const updatedCart = [];
  const updatedCartProducts = [];
  state.cart_products.map(product => {
    if(product.id !== action.payload.product_id) {
      updatedCartProducts.push({...product})
    }
  })
  console.log(action.payload.product_id, updatedCartProducts)
  state.cart_items.map((cartItem) => {
    if (cartItem.id !== action.payload.cart_item_id) {
      updatedCartProducts.push({ ...cartItem });
    }
  })
  console.log(action.payload.cart_item_id, updatedCart)
  return updateObject(state, {
    cart_items: [...updatedCart],
    cart_products: [...updatedCartProducts],
  });
};

const fetchCartFailed = (state, action) => {
  console.log(state, action.payload)
  return updateObject(state,{ error: true, loading: false})
} 

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.SET_CART:
      return setCart(state, action);
    case actionTypes.ADD_PRODUCT_TO_CART:
      return addProductToCart(state, action);
    case actionTypes.REMOVE_PRODUCT_FROM_CART:
      return removeProductFromCart(state, action);
    case actionTypes.FETCH_CART_FAILED:
      return fetchCartFailed(state, action);
    case actionTypes.START_FETCH_CART:
      return startFetchCart(state, action);
    default:
      return state;
  }
};

export default reducer;
