 return {
   type: actionTypes.USER_PLAN_START
 }
}

export const userPlanDelete = (member_id, plan_id) => {
  return {
    type: actionTypes.USER_PLAN_DELETE,
  };
};


export const initPlanMembership = (plan_id) => async (dispatch, getState) =>{
  console.log('in initPlanMembership')
  let user = getState().auth.userId
  dispatch(userPlanStart());
  const planData = {
    member_id: user.id,
    plan_id: plan_id
  }
  await api
  .post(`/users/${user}/plan_membership`, planData)
  .then(response => {
    console.log(response.data)
    dispatch(userPlanSuccess(response))
  })
  // .catch(err => {
  //   dispatch(userPlanFail(err.message))
  // })
 }

export const userPlanSuccess = (plan_id) => {
  return {
   type: actionTypes.USER_PLAN_SUCCESS, plan: plan_id
  }
}

export const userPlanFail = (error) => {
  return {
    type: actionTypes.USER_PLAN_FAIL, error: error
  }
}
