import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import { Link } from "react-router-dom";

const ProductCard = (props) => {


    console.log(props);
    let img = [...props.product.images.split(",")[0]];
    return (
      <li className='col span-1-of-3'>
        <div className='product-card'>
          <div className='col product-outer'>
            <div className='product-photo'>
              <img alt={props.product.name} src={img} />
            </div>
            <React.Fragment>
              <ul className='product-card-details'>
                <li className='product-title'>
                  <li>{props.brand.name}</li>
                </li>

                <Link
                  className='product-subtitle'
                  to={`/products/${props.product.id}`}
                >
                  <li className='product-subtitle'>
                    {props.product.name}
                  </li>
                </Link>

                <li className='product-subtitle'>
                  <li>{props.product.price_string}</li>
                </li>
              </ul>
            </React.Fragment>
          </div>
        </div>
      </li>
    );
  }


// export default ProductCard

const mapStateToProps = (state, ownProps) => {
  console.log(state, ownProps);
  return { favorites: state.favorite.select,
  products: state.favorite.products};
};

// const mapDispatchToProps = (dispatch) => {
//   return {
//     fetchShowProductSizes: (brand_id, product_id) =>
//       dispatch(actions.fetchShowProductSizes(brand_id, product_id)),
//   };
// };

export default connect(mapStateToProps)(ProductCard);
