import React from "react";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
import { Link } from "react-router-dom";

class ProductCard extends React.PureComponent {


  render() { 
    console.log(this.props);
    let img
    if (this.props.product !== "undefined" && this.props.product !== undefined)
    img = this.props.product.images.split(",")[0];
      return (
      <li className='col span-1-of-3'>
        <div className='product-card'>
          <div className='col product-outer'>
            <div className='product-photo'>
              <img alt={this.props.product.name} src={img} />
            </div>
            <React.Fragment>
              <ul className='product-card-details'>
                <li className='product-title'>
                  <li>{this.props.brand.name}</li>
                </li>

                <Link
                  className='product-subtitle'
                  to={`/products/${this.props.product.id}`}
                >
                  <li className='product-subtitle'>
                    {this.props.product.name}
                  </li>
                </Link>

                <li className='product-subtitle'>
                  <li>{this.props.product.price_string}</li>
                </li>
              </ul>
            </React.Fragment>
          </div>
        </div>
      </li>
    )}
  }


// export default ProductCard

const mapStateToProps = (state, ownProps) => {
  console.log(state, ownProps);
  return { favorites: state.favorite.select,
  products: state.product.select};
};

// const mapDispatchToProps = (dispatch) => {
//   return {
//     fetchShowProductSizes: (brand_id, product_id) =>
//       dispatch(actions.fetchShowProductSizes(brand_id, product_id)),
//   };
// };

export default connect(mapStateToProps)(ProductCard);
