import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";

import Favorite from "./Favorite";

class Favorites extends React.Component {
  componentDidMount() {

    this.init()
    console.log(this.props)
  }

  init = async () => {
    await this.props.initFavorites(this.props.userId);
    await this.props.initBrands();
  }

  // fetchFaveBrands = ()

  findBrand = (product) => {
    console.log(this.props)
    let brand = this.props.brands.filter(
      (brand) => brand.id === product.brand_id
    );
    return brand;
  };

  renderList = () => {
    console.log("render list", this.props.brands);
    return !this.props.loading && !this.props.error ? 
     this.props.products.map((fave) => {
      let brand = [...this.findBrand(fave)]
      return (
        <Favorite
            brand={brand}
            key={fave.id}
            product={fave}
          />
      );
    }) : <div>loading...</div>
  };

  render() {
    // console.log(this.props);
    return (
        <div>
          <div className="section-products">
              {this.renderList()}
          </div>
        </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state);
  return {
    favorites: state.favorite.favorites,
    products: state.favorite.products,
    brands: state.brand.brands,
    userId: state.auth.userId,
    error: state.favorite.error,
    loading: state.favorite.loading
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initFavorites: (userId) => dispatch(actions.initFavorites(userId)),
    setShowProduct: () => dispatch(actions.setShowProduct()),
    initBrands: () => dispatch(actions.initBrands())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
