import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

const initialState = {
  select: [],
  show: [],
  error: null,
  loading: false,
  message: null
};

const setShowBrand = (state, action) => {
  console.log(action.brand);
  return updateObject(state, {
    show: { ...action.brand },
    error: null,
    loading: false,
    message: null
  });
};

const setBrands = (state, action) => {
  console.log("in set brands", state, action)
  return updateObject( state, { select: [...action.brands ], error: null, loading: false, message: ''});
};

const fetchBrandsFailed = (state, action) => {
  return updateObject(state, {error: true, loading: false, message: action.payload})
}

export const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.SET_BRANDS:
      return setBrands(state, action);
    case actionTypes.SET_SHOW_BRAND:
      return setShowBrand(state, action);
    case actionTypes.FETCH_BRANDS_FAILED:
      return fetchBrandsFailed(state, action);
    default:
      return state;
  }
};

export default reducer
