import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

const initialState = {
  favorites: []
}

export default (state = [], action) => {
  switch (action.type) {
    case "DELETE_FAVORITE":
      return action.payload
    case "CREATE_FAVORITE":
      return action.payload;
    default:
      return state;
  }
}

const updateFavorites = (state, action ) => {
  const updatedFavorites = [...state, action.payload]
  return updateObject(state, {
    favorites: updatedFavorites
  })
}

const deleteFavorite = (state, action) => {
  return updateObject(state, {
    favorites: state.favorites.filter(fave => fave.id !== action.payload.id)
  })
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.CREATE_FAVORITE:
      return updateFavorites(state, action)
      case actionTypes.DELETE_FAVORITE:
        return deleteFavorite(state, action)
        default:
          return state
  }
}
