import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import ProductCard from './ProductCard'
import { Link } from "react-router-dom";


class Favorite extends React.Component {
  componentDidMount() {
    this.props.fetchProdById(this.props.fave.product_id);
  }

  render() {
    console.log(this.props);
    return (

      <div className='section-products'>
        <div className='col product-outer'>
          <div className='product-card'>
            <ul className='product-card-details'>
              <li className='product-title'>
                <li>{this.props.brand.name}</li>
              </li>
              <Link
                className='product-subtitle'
                to={`/products/${this.props.product.id}`}
              >
                <li className='product-subtitle'>{this.props.product.name}</li>
              </Link>
              <li className='product-subtitle'>
                <li>{this.props.product.price_string}</li>
              </li>
            </ul>
            <li>{this.props.brand.name}</li>
            <li>{this.props.product.name}</li>
            <li>{this.props.product.price_string}</li>
            <img src={this.props.product.images} alt='' />
            <button>Delete Favorite</button>
            <button>See Product</button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state.productState, ownProps);
  return {
    product: state.productState.selectedProduct,
    brand: state.productState.selectProdBrand,
    sizes: state.productState.selectProdSizes,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    cart: (product_id) => dispatch(actions.addProductToCart(product_id)),
    fetchProdById: (product_id) => dispatch(actions.fetchProdById(product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorite);
