import api from '../apis/api'
import _ from 'lodash'
import * as actionTypes from "./actionTypes";

 export const fetchProdByBrand = (brand_id) => async dispatch => {
    const response = await api.get(`/brands/${brand_id}/products`)
    dispatch({type: actionTypes.FETCH_PRODUCTS_BY_BRAND, payload: response.data})
 }

 export const fetchProdByCategory = (category) => async (dispatch) => {
   const response = await api.get(`/products/`, {
     params: {
       category: category,
     },
   });
   dispatch({ type: actionTypes.FETCH_PRODUCTS_BY_CATEGORY, payload: response.data });
 };

 export const fetchProdBySize = (size) => async (dispatch) => {
   const response = await api.get(`/products/`, {
     params: {
       size: size,
     },
   });
   dispatch({ type: actionTypes.FETCH_PRODUCTS_BY_SIZE, payload: response.data });
 };

 export const fetchAllProd = () => async dispatch => {
    const response = await api.get(`/products/`)
    dispatch({type: actionTypes.FETCH_ALL_PRODUCTS, payload: response.data})
 }

export const fetchProdByNumber = (product_number) =>  async dispatch => {
  const response = await api.get(`/products/${product_number}`)
  dispatch({type: actionTypes.FETCH_PRODUCTS_BY_NUM, payload: response.data })
 };

  export const fetchProdById = (prod_id) => async dispatch => {
     const response = await api.get(`products/${prod_id}`)
     console.log(response.data)
     dispatch({ type: actionTypes.FETCH_PROD_BY_ID, payload: response.data });
  }

export const fetchBrand = (brand_id) => async (dispatch) => {
  const response = await api.get(`/brands/${brand_id}`);
  dispatch({ type: actionTypes.FETCH_BRAND, payload: response.data });
};

export const fetchProdAndBrands = () => async (dispatch, getState) => {
  await dispatch(fetchAllProd());

  _.chain(getState().products)
    .map("brand_id")
    .uniq()
    .forEach((id) => dispatch(fetchBrand(id)))
    .value();
};
