import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

class AppHeader extends React.Component {
  state = {
    isLoggedIn: false
  }

  switchLoggedInModeHandler = () => {
    this.setState(prevState => {
      return { isLoggedIn: !prevState.isLoggedIn}
    }, console.log(this.state))
  }


  render() {
    let signUpLink = null;
    if (!this.state.loggedIn) {
      signUpLink =   <Link to='/signup'>
                <li id="button">SIGN UP</li>
              </Link>
    } else {
      signUpLink = <Link to="/logout"><li id="button">SIGN OUT</li></Link>
    }

    return (
    <nav className='nav-container'>
      <div className='row'>
        <div>
          <ul className='main-nav'>
            <li>
              <Link to='/brands'><li id="button">DESIGNERS</li></Link>
            </li>
            <li>
              <Link to='/products'><li id="button">CLOTHING</li></Link>
            </li>
            <li>
              <Link to='/products'><li id="button">ACCESSORIES</li></Link>
            </li>
            <li>
              <Link to='/plans'><li id="button">PLANS</li></Link>
            </li>
          </ul>
          <ul className='user-menu'>
            <li>
              <Link to='/favorites'>
                <ion-icon size='large' name='heart-outline'></ion-icon>
              </Link>
            </li>
            <li>
              <Link to='/login'>
                <ion-icon size='large' name='person-outline'></ion-icon>
              </Link>
            </li>
            <li>
              <Link to='/cart'>
                <ion-icon size='large' name='cart-outline'></ion-icon>
              </Link>
            </li>
            <li>

              {signUpLink}
            </li>
          </ul>
        </div>
      </div>
    </nav>
  )}
};

const mapStateToProps = (state) => {
  return {
    current_user: this.state.current_user
  }
}

export default connect(mapStateToProps)(AppHeader)
