import api from "../apis/api";
import * as actionTypes from "./actionTypes";
import * as actions from "../actions/index";

export const fetchShowBrand = (brand_id) => async (dispatch) => {
  console.log("in fetch brand");

  await api.get(`/brands/${brand_id}`).then((resp) => {
    dispatch({ type: actionTypes.SET_SHOW_BRAND, brand: resp.data });
  });
};

export const initBrands = () => async (dispatch) => {
  await api
    .get(`/brands/`)
    .then((resp) => {
      dispatch(setBrands(resp.data));
    })
    .catch((error) => {
      dispatch(fetchBrandsFailed(error));
    });
};

export const setBrands = (brands) => {
  return {
    type: actionTypes.SET_BRANDS,
    brands: brands
  }
}

export const fetchBrandsFailed = (error) => {
  return {
    type: actionTypes.FETCH_BRANDS_FAILED,
    payload: error,
  };
};
