import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

const initialState = {
  sizes: []
}


const setShowProductSizes = (state, action) => {
  console.log('hi', state)
  return updateObject(state, {
      sizes: [...action.sizes],
    })
  }

export const reducer = (state = initialState, action) => {
  console.log(state)
  switch (action.type) {
    case actionTypes.SET_SHOW_PRODUCT_SIZES:
      return setShowProductSizes(state, action)
      default:
        return state;
  }
}
