import React from "react";
import { connect } from "react-redux";
import * as actions from "../../store/actions/index";
import  ProductCard  from "./ProductCard";
import {ProductNav} from './ProductNav'

class ProductIndex extends React.Component {
  componentDidMount() {
    this.props.fetchAllProd();
    this.props.fetchBrands()
  }
  findBrand =  (product) => {
    let brand = this.props.brands.filter(
      (brand) => brand.id === product.brand_id
    );
      return brand[0]
  };

  renderList = () => {
    return this.props.products.map((prod) => {
      return (
        <ProductCard
          brand={this.findBrand(prod)}
          key={prod.id}
          product={prod}
        />
      );
    });
  };

  render () {
    return (
      <div>
        <ProductNav/>
        <div className='section-products'>
          {" "}
          <br />
          {this.renderList()}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return { products: state.productState.productSelection, brands: state.brandState.brands };
};


mapDispatchToProps = (dispatch) => {
  return {
    fetchAllProd: dispatch(actions.fetchAllProd),
    fetchBrands: dispatch(actions.fetchBrands)
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(ProductIndex);
