import * as actionTypes from "../actions/actionTypes";
import { updateObject } from "../utility";

const initialState = {
  favorites: [],
}

const updateFavorites = (state, action ) => {
  const updatedFavorites = [...state.favorites, action.payload]
  return updateObject(state, {
    favorites: updatedFavorites
  })
}

const deleteFavorite = (state, action) => {
  const updatedFavorites = [...state.favorites.filter(fave => fave.id !== action.payload.id)]
  return updateObject(state, {
    favorites: updatedFavorites
  })
}

const fetchFavorites = (state, action) => {
  const updatedFavorites = action.payload
  console.log(updatedFavorites)
  return updateObject(state, {favorites:  updatedFavorites})
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.CREATE_FAVORITE:
      return updateFavorites(state, action)
      case actionTypes.DELETE_FAVORITE:
        return deleteFavorite(state, action)
        case actionTypes.FETCH_FAVORITES:
          return fetchFavorites(state, action)
        default:
          return state
  }
}


export default reducer
