import React, {useState} from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";
const PlanCard = (props) => {
  const [show, setShow] = useState()


  const clickHandler = (e) => {
    e.preventDefault();
    props.initPlanMembership(props.plan.id).then(signUpHandler());
  };

  const signUpHandler = () => {
    console.log(props.error_message);
    let signUpResponse;
    if (props.error === true && props.error_message !== "undefined") {
      signUpResponse = (
        <Link to='/plans/update'>
          <div>{props.error_message}</div>
        </Link>
      );
    }
    return signUpResponse;
  };

  const renderText = () => {
    let text 
    if (props.current_user.subscribed === true && props.plan.id === props.current_user.plan_membership.plan_id){
      text = "Current Plan"
    } else { text = "Choose Plan"}
    return text
  };

  const renderId = () => {
    let id;
    if (
      props.current_user.subscribed === true &&
      props.plan.id === props.current_user.plan_membership.plan_id
    ) {
      id = "current-plan";
    } else {
      id = "plan-option";
    }
    return id;
  };

  return (
    <div className='col span-1-of-3'>
      <div className='plan-card'>
        <div className='col plan-outer'></div>
        <React.Fragment>
          {/* <div>{signUpHandler()}</div> */}
          <ul className='plan-card-details'>
            <li className='plan-title'>{props.plan.name}</li>
            <li>{props.plan.description}</li>
            <li className='plan-price'>{props.plan.price_string}/month</li>
          </ul>
        </React.Fragment>
      </div>
      <span>
        {props.token ? (
          <button
            id={renderId()}
            onClick={(e) => clickHandler(e)}
            value='TRY NOW'
            plan={props.plan.id}
          ><signUpHandler>

            {renderText()}
          </signUpHandler>
          </button>
        ) : (
          <Link to='/signup'>
            <button plan={props.plan.id}value='TRY NOW'>TRY NOW</button>
          </Link>
        )}
      </span>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    token: state.auth.token,
    current_user: state.auth.current_user,
    error: state.plan.error,
    error_message: state.plan.message,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initPlanMembership: (plan_id) =>
      dispatch(actions.initPlanMembership(plan_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PlanCard);
