  // };

  init =  () => {
    this.props.initFavorites(this.props.userId);
    this.props.initBrands();
  }
  findBrand = (product) => {
    console.log(this.props)
    let brand = this.props.brands.filter(
      (brand) => brand.id === product.brand_id
    );
    return brand;
  };

  renderList = () => {
     this.props.products.map((fave) => {
      let brand = this.findBrand(fave)
      console.log(fave, brand)
      return (
        <Favorite
            brand={brand}
            key={fave.id}
            product={fave}
          />
      );
     })
  };

  render() {
    // let renderFavorites = null;
    // if (!this.state.favorites.length <= 0) {
    //   renderFavorites = this.renderList()
    // } else if (this.state.favorites.length > 0) {
    //   renderFavorites = <div>no user found</div>
    // }
    // console.log(this.props);
    return (
      <div>
        <div className='section-products'>{this.renderList()}</div>
      </div>
    );
  }
}

// export default Favorites;

const mapStateToProps = (state, ownProps) => {
  console.log(state);
  return {
    favorites: state.favorite.favorites,
    products: state.favorite.products,
    brands: state.brand.select,
    userId: state.auth.userId,
    error: state.favorite.error,
    loading: state.favorite.loading
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initFavorites: (userId) => dispatch(actions.initFavorites(userId)),
    setShowProduct: () => dispatch(actions.setShowProduct()),
    initBrands: () => dispatch(actions.initBrands())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
