import React from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../store/actions/index";

class AppHeader extends React.Component {
  render() {
    let signUpLink = null;
    if (!this.props.current_user) {
      signUpLink = (
        <li>
          <Link to="/login"><li id="button">SIGN UP | LOG IN</li></Link>
        </li>
      );
    } else {
      signUpLink = 
        <li onClick={(e) => this.props.logout(e)} id='button'>
          SIGN OUT
        </li>
    }

    return (
      <nav className='nav-container'>
        <div className='row'>
          <div>
            <ul className='main-nav'>
              <li>
                <Link to='/brands'>
                  <li id='button'>DESIGNERS</li>
                </Link>
              </li>
              <li>
                <Link to='/products'>
                  <li id='button'>CLOTHING</li>
                </Link>
              </li>
              <li>
                <Link to='/products'>
                  <li id='button'>ACCESSORIES</li>
                </Link>
              </li>
              <li>
                <Link to='/plans'>
                  <li id='button'>PLANS</li>
                </Link>
              </li>
            </ul>
            <ul className='user-menu'>
              <li>
                <Link to='/favorites'>
                  <ion-icon size='large' name='heart-outline'></ion-icon>
                </Link>
              </li>
              <li>
                <Link to='/login'>
                  <ion-icon size='large' name='person-outline'></ion-icon>
                </Link>
              </li>
              <li>
                <Link to='/cart'>
                  <ion-icon size='large' name='cart-outline'></ion-icon>
                </Link>
              </li>
              <li>{signUpLink}</li>
            </ul>
          </div>
        </div>
      </nav>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    current_user: state.current_user,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    logout: () => dispatch(actions.logout()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(AppHeader);
