import React from "react";
import { connect } from "react-redux";
import AccountInfo from "./AccountInfo";
import OrderContainer from "./OrderContainer";
import api from "../store/apis/api";

class Profile extends React.Component {

  componentDidMount = () => {
    this.fetchUser()
  }

  fetchUser = () => {
    api.get(`/users/${this.props.current_user.id}`).then(response => setState({user: response.data}))
  }
  renderPlan = () => {
    let value;
    if (this.props.current_user && this.props.current_user.subscribed) {
      value = <div>Your Plan: {this.props.current_user.plan_membership.plan_name}</div>;
    } else {
      value = <div>Choose A Plan</div>;
    }
    return value;
  };

  renderUser = () => {if (this.props.current_user) {
    return(
  <div>
       <div>Welcome, {this.props.current_user.name}</div>
    </div>)

  } else if (!this.props.current_user) {
    return <div>no user found</div>;
  }}

  render() {return (
    <div>
      <div>{this.renderUser()}</div>
      <div>{this.renderPlan()}</div>
      <OrderContainer/>
      <div>
        <p className='title'>Profile</p>
        <AccountInfo />
      </div>
</div>
  )};
};
const mapStateToProps = (state) => {
  return {
    userId: state.auth.userId,
    current_user: state.auth.current_user,
    favorites: state.favorite.favorites,
    products: state.favorite.products,
    loading: state.auth.loading,
    cart: state.cart.cart,
    cart_items: state.cart.cart_items,
  };
};

export default connect(mapStateToProps)(Profile);

// export default Profile
