import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";

class Favorite extends React.Component {
  componentDidMount() {
    //this.props.fetchProd(this.props.fave.product_id)
  }

  render() {
    console.log(this.props.brand);
    return (
      <div className='section-products'>
        <div className='col product-outer'>
          <div className='product-card'>
            <ul className='product-card-details'>
              <li className='product-title'>
                <li>{this.props.brand.name}</li>
              </li>
              <Link
                className='product-subtitle'
                to={`/products/${this.props.product.id}`}
              >
                <li className='product-subtitle'>{this.props.product.name}</li>
              </Link>
              <li className='product-subtitle'>
                <li>{this.props.product.price_string}</li>
              </li>
            </ul>
            <li>{this.props.brand.name}</li>
            <li>{this.props.product.name}</li>
            <li>{this.props.product.price_string}</li>
            <img src={this.props.product.images} alt='' />
            <button>Delete Favorite</button>
            <button>See Product</button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  console.log(state, ownProps);
  return {
    product: state.productState.productSelection.filter(
      (product) => product.id === ownProps.fave.product_id)[0],
      brand: state.brandState.brands.filter(brand => brand.id === ownProps.product[0].brand_id)[0]
    ,
  };
};

const mapDispatchToProps = (dispatch) => {
  return { fetchProd: (prod_id) => dispatch(actions.fetchProdById(prod_id)) };
};
export default connect(mapStateToProps, mapDispatchToProps)(Favorite);
