import React from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import * as actions from "../store/actions/index";

const PlanCard = (props) => {
  const clickHandler = (e) => {
    e.preventDefault();
    console.log(e.target.value)
    if (props.current_user !== null && props.current_user.subscribed === false)
      signUp();

    if (props.current_user !== null && props.current_plan === null) signUp();

    if (props.current_user !== null && props.current_plan !== null)
      deletePlan();
  };

  const signUp = () => {
    props.initPlanMembership(props.plan.id);
  };

  const deletePlan = () => {
    props.userPlanDelete();
  };

  const renderText = () => {
    //guests
    if (props.current_plan === undefined || props.current_plan === null)
      return "Try Now";
    //members with or without plan membership
    return !props.current_plan.plan_membership ||
      props.plan.id !== props.current_plan.plan_membership.plan_id
      ? "Try Now"
      : "Current Plan";
  };

  const renderId = () => {
    //guests
    if (props.current_plan === undefined || props.current_plan === null) {
      console.log("hi in renderId plan option", props.current_plan);
      return "plan-option";
    }
    //members with or without plan membership
    if (props.current_plan.plan_membership !== undefined || props.current_plan.plan_membership !== null) {
      console.log("hi in renderId current_plan", props.plan.id, props.current_plan);
      return props.plan.id !== props.current_plan.plan_membership.plan_id
        ? "plan-option"
        : "current-plan";
    }
  };

  return (
    <div className='plan-card'>
      <React.Fragment>
        <div className='plan-card-details'>
          <ul className='plan-title'>Up to</ul>
          <p>{props.plan.items} </p>
          <span>items per month</span>
          <li className='plan-description'>{props.plan.description}</li>
        </div>
        <hr id='plan-hr' />
      </React.Fragment>
      <div className='plan-card-features'>
        <ul className='plan-bullet-points'>
          {props.plan.features.split(",").map((feature) => {
            return (
              <li>
                <ion-icon name='checkmark'></ion-icon>
                <span>{feature}</span>
              </li>
            );
          })}
        </ul>
      </div>
      <div id='plan-button'>
        {props.current_user ? (
          <button
            plan={props.plan.id}
            id={renderId()}
            value='button'
            onClick={(e) => clickHandler(e)}
          >
            {renderText()}
          </button>
        ) : (
          <NavLink to='/login' activeStyle={{ color: "white" }}>
            <button plan={props.plan.id} id={renderId()} value='button'>
              TRY NOW
            </button>
          </NavLink>
        )}
      </div>
      <div className='plan-price'>
        <span>{props.plan.price_string}</span> <p>/month</p>
      </div>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    current_user: state.auth.current_user,
    current_plan: state.plan.current_plan,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initPlanMembership: (plan_id) =>
      dispatch(actions.initPlanMembership(plan_id)),
    userPlanDelete: () => dispatch(actions.userPlanDelete()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(PlanCard);
