import React from "react";
import * as actions from "../store/actions/index";
import { connect } from "react-redux";
import ProductCard from './ProductCard'
import Favorite from "./Favorite";

class Favorites extends React.Component {
  componentDidMount() {
    this.props.initFavorites(23)
  }

  findBrand = (product) => {
    let brand = this.props.brands.filter(
      (brand) => brand.id === product.brand_id
    );
    return brand[0];
  };

  renderList = () => {
    console.log("render list", this.props.favorites);
    return this.props.favorites.map((fave) => {
      return (
        <ProductCard
          brand={this.findBrand(fave)}
          key={fave.id}
          product={fave}
        />
      );
    });
  };

  render() {
    // console.log(this.props);
    return (
      <div className='section-products'>
        {" "}
        <br />
        {this.renderList()}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return {
    favorites: state.favorite.favorites,
    products: state.product.select,
    brands: state.brand.brands,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    initFavorites: (user_id) => dispatch(actions.initFavorites(user_id)),
    setShowProduct: (product_id) => dispatch(actions.setShowProduct(product_id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Favorites);
