import React from "react";
import { connect } from "react-redux";
import api from "../apis/api";
import * as actions from "../store/actions/index";
import  ProductCard  from "./ProductCard";
import ProductNav from './ProductNav'

class ProductIndex extends React.Component {

    state = {
    products: [],
    brands: [],
  }

  componentDidMount() {
    // this.props.initBrands()
    // this.props.initProducts()
    this.fetchBrands()
    this.fetchProducts()
  }
    fetchBrands = () => {
      api.get(`/brands/`).then(resp => this.setState({brands: resp.data }))

    }
    fetchProducts = () => {
      api.get(`/products/`).then((resp) => this.setState({ products: resp.data }));
    };

  findBrand =  (product) => {
    console.log(this.props.brands)
    let brand = this.props.brands.filter(
      (brand) => brand.id === product.brand_id
    );
      return brand[0]
  };

  renderList = () => {

    return this.props.products.map((prod) => {
      return (
        <ProductCard
          brand={this.findBrand(prod)} 
          key={prod.id}
          product={prod}
          sizes={this.props.fetchShowProductSizes(prod.brand_id, prod.id)}
        />
      );
    });
  };

  render () {
    return (
      <div>
        <ProductNav brands={this.props.brands}/>
        <div className='section-products'>
          {" "}
          <br />
          {this.renderList()}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state);
  return { products: state.product.select, brands: state.brand.select,
  error: state.product.error };
};


const mapDispatchToProps = (dispatch) => {
  return {
    initProducts: () => dispatch(actions.initProducts()),
    initBrands: () => dispatch(actions.initBrands()),
    fetchShowProductSizes: (brand_id, product_id) => dispatch(actions.fetchShowProductSizes(brand_id, product_id))
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(ProductIndex);
